export const getPrice = (price, size) => {
  switch (size) {
    case 0:
    default:
      return price;
    case 1:
      return price + 1.5;
    case 2:
      return price + 3.5;
  }
};

export const getMyOrder = (items) => {
  const order = [];
  Object.values(items).map((item) => {
    item.map((pizza) => {
      order.push(`Pizza ${pizza.name}: size ${pizza.size}`);
    })
    console.log(
      item.map((pizza) => {
        order.push(`Pizza ${pizza.name}: size ${pizza.size}`);
      })
    );
  });
  return order.join("; ");
};
