export const getPrice = (price, size) => {
  switch (size) {
    case 0:
    default:
      return price;
    case 1:
      return price + 1.5;
    case 2:
      return price + 3.5;
  }
};

export const getMyOrder = (items) => {
  return Object.values(items).map((item) => {
    console.log(item);
    return item
  });
};
